import '../frontend/style.css';

const PLUGIN_PATH = "/password-lock-manager";
let isLocked = false;
let encryptedPasscode = "";
let failedAttempts = 0;
const MAX_ATTEMPTS = 3;

// base64 encoded MP3 files
const lockSound = "data:audio/mp3;base64,"
const unlockSound = "data:audio/mp3;base64,";

const playSound = (soundData) => {
  const audio = new Audio(soundData);
  audio.play().catch(error => console.error("Error playing sound:", error));
};

const encrypt = (text) => {
  return btoa(text.split('').map(char => String.fromCharCode(char.charCodeAt(0) + 1)).join(''));
};

const decrypt = (encoded) => {
  return atob(encoded).split('').map(char => String.fromCharCode(char.charCodeAt(0) - 1)).join('');
};

const createMessage = (text, type = 'info') => {
  const message = document.createElement("div");
  message.className = `message ${type}`;
  message.textContent = text;
  message.style.position = "fixed";
  message.style.top = "20px";
  message.style.left = "50%";
  message.style.transform = "translateX(-50%)";
  message.style.zIndex = "10001";
  setTimeout(() => message.remove(), 3000);
  return message;
};

const createLockScreen = () => {
  const lockScreen = document.createElement("div");
  lockScreen.className = "lock-screen";
  lockScreen.style.display = "none";
  lockScreen.style.position = "fixed";
  lockScreen.style.top = "0";
  lockScreen.style.left = "0";
  lockScreen.style.width = "100%";
  lockScreen.style.height = "100%";
  lockScreen.style.backgroundColor = "rgba(0, 0, 0, 0.9)";
  lockScreen.style.zIndex = "9999";
  lockScreen.style.display = "none";
  lockScreen.style.flexDirection = "column";
  lockScreen.style.justifyContent = "center";
  lockScreen.style.alignItems = "center";

  const lockIcon = document.createElement("i");
  lockIcon.className = "fas fa-lock lock-icon";
  lockScreen.appendChild(lockIcon);

  const passcodeDisplay = document.createElement("div");
  passcodeDisplay.className = "passcode-display";
  passcodeDisplay.textContent = "Enter Passcode";

  const buttonContainer = document.createElement("div");
  buttonContainer.className = "button-container";

  for (let i = 1; i <= 9; i++) {
    const button = document.createElement("button");
    button.textContent = i;
    button.onclick = () => updatePasscodeDisplay(i);
    buttonContainer.appendChild(button);
  }

  const deleteButton = document.createElement("button");
  deleteButton.innerHTML = '<i class="fas fa-backspace"></i>';
  deleteButton.onclick = () => {
    const currentPasscode = passcodeDisplay.getAttribute('data-passcode') || '';
    if (currentPasscode.length > 0) {
      passcodeDisplay.setAttribute('data-passcode', currentPasscode.slice(0, -1));
      updatePasscodeDisplay();
    }
  };
  
  const zeroButton = document.createElement("button");
  zeroButton.textContent = "0";
  zeroButton.onclick = () => updatePasscodeDisplay(0);

  const enterButton = document.createElement("button");
  enterButton.innerHTML = '<i class="fas fa-arrow-right"></i>';
  enterButton.onclick = checkPasscode;

  buttonContainer.appendChild(deleteButton);
  buttonContainer.appendChild(zeroButton);
  buttonContainer.appendChild(enterButton);

  lockScreen.appendChild(passcodeDisplay);
  lockScreen.appendChild(buttonContainer);

  return lockScreen;
};

const updatePasscodeDisplay = (digit) => {
  const passcodeDisplay = document.querySelector('.passcode-display');
  let currentPasscode = passcodeDisplay.getAttribute('data-passcode') || '';
  
  if (digit !== undefined && currentPasscode.length < 6) {
    currentPasscode += digit;
  }
  
  passcodeDisplay.setAttribute('data-passcode', currentPasscode);
  passcodeDisplay.textContent = '•'.repeat(currentPasscode.length);
};

const checkPasscode = () => {
  const passcodeDisplay = document.querySelector('.passcode-display');
  const enteredPasscode = passcodeDisplay.getAttribute('data-passcode') || '';
  if (enteredPasscode === decrypt(encryptedPasscode)) {
    isLocked = false;
    failedAttempts = 0;
    unlockScreen();
    playSound(unlockSound);
    document.body.appendChild(createMessage("Screen unlocked successfully!", 'success'));
    setupLockListener();
  } else {
    failedAttempts++;
    if (failedAttempts >= MAX_ATTEMPTS) {
      closeApp();
    } else {
      document.body.appendChild(createMessage(`Incorrect passcode! ${MAX_ATTEMPTS - failedAttempts} attempts left.`, 'error'));
    }
  }
  passcodeDisplay.setAttribute('data-passcode', '');
  passcodeDisplay.textContent = "Enter Passcode";
};

const unlockScreen = () => {
  const lockScreen = document.querySelector('.lock-screen');
  if (lockScreen) {
    lockScreen.style.display = "none";
  }
  document.removeEventListener('keydown', preventKeyboardInput, true);
};

const closeApp = (reason = "Too many failed attempts. The app has been locked for security reasons.") => {
  const closeScreen = document.createElement("div");
  closeScreen.className = "close-screen";
  closeScreen.innerHTML = `
    <i class="fas fa-exclamation-triangle"></i>
    <h2>App Locked</h2>
    <p>${reason}</p>
    <p>Please contact your administrator to unlock the application.</p>
  `;
  document.body.innerHTML = '';
  document.body.appendChild(closeScreen);
};

const preventKeyboardInput = (e) => {
  e.preventDefault();
  e.stopPropagation();
};

const setupLockListener = () => {
  const lockKeyHandler = (e) => {
    if (e.key === "F12") {
      e.preventDefault();
      if (encryptedPasscode) {
        isLocked = true;
        failedAttempts = 0;
        lockScreen();
        playSound(lockSound);
      } else {
        document.body.appendChild(createMessage("Please set a passcode first.", 'error'));
      }
    }
  };

  document.addEventListener("keydown", lockKeyHandler);
};

const lockScreen = () => {
  const lockScreen = document.querySelector('.lock-screen');
  if (lockScreen) {
    lockScreen.style.display = "flex";
  }
  document.addEventListener('keydown', preventKeyboardInput, true);
};

const createPage = (sdk) => {
  const container = document.createElement("div");
  container.className = "password-lock-manager";

  const header = document.createElement("h1");
  header.textContent = "Password Lock Manager";
  container.appendChild(header);

  const setPasscodeInput = document.createElement("input");
  setPasscodeInput.type = "password";
  setPasscodeInput.maxLength = 6;
  setPasscodeInput.className = "set-passcode-input";
  setPasscodeInput.placeholder = "Set 6-digit passcode";
  container.appendChild(setPasscodeInput);

  const setPasscodeButton = document.createElement("button");
  setPasscodeButton.textContent = "Set Passcode";
  setPasscodeButton.className = "set-passcode-button";
  container.appendChild(setPasscodeButton);

  setPasscodeButton.onclick = () => {
    if (setPasscodeInput.value.length === 6 && /^\d+$/.test(setPasscodeInput.value)) {
      encryptedPasscode = encrypt(setPasscodeInput.value);
      setPasscodeInput.value = "";
      document.body.appendChild(createMessage("Passcode set successfully! You can now lock your screen with F12.", 'success'));
    } else {
      document.body.appendChild(createMessage("Please enter a 6-digit numeric passcode.", 'error'));
    }
  };

  const lockScreen = createLockScreen();
  document.body.appendChild(lockScreen);

  setupLockListener();

  sdk.navigation.addPage(PLUGIN_PATH, {
    body: container,
  });
};

export const init = (sdk) => {
  createPage(sdk);
  sdk.sidebar.registerItem("Password Lock", PLUGIN_PATH, {
    icon: "fas fa-lock",
  });
};
